// Returns the value set by CG_TOGG
bool is_mac_the_default(void) { return keymap_config.swap_lctl_lgui; }
bool is_shift_held(void) { return (get_mods() & MOD_BIT(KC_LSFT)) || (get_mods() & MOD_BIT(KC_RSFT)); }
bool is_ctrl_held(void) { return get_mods() & MOD_BIT(KC_LCTL); }
bool is_gui_held(void) { return get_mods() & MOD_BIT(KC_LGUI); }

void send_mac_or_win(uint16_t mac_code, uint16_t win_code, bool is_pressed) {
  uint16_t code = is_mac_the_default() ? mac_code : win_code;
  if (is_pressed) {
    register_code16(code);
  } else {
    unregister_code16(code);
  }
}

bool process_record_user(uint16_t keycode, keyrecord_t *record)
{
  bool isPressed = record->event.pressed;
  bool isHeld = !record->tap.count && isPressed;

  /* { */
  /*   uint16_t mod = is_mac_the_default() ? KC_LGUI : KC_LALT; */
  /* } */

  switch (keycode) {
  case LT(0,KC_R): {
    uint16_t code = is_mac_the_default() ? G(KC_TAB) : A(KC_TAB);
    if (isHeld) {
      register_code16(code);
    } else if (!record->tap.count) {
      unregister_code16(code);
      return false;
    }
    return true;
  }
  case LT(0,KC_SCLN):
    if (isHeld) {
      tap_code16(KC_COLN);
      return false;
    }
    return true;
  case LT(0,KC_BSLS):
    if (isHeld) {
      tap_code16(KC_HOME);
      return false;
    }
    return true;
  case LT(0,CT_PIPE):
    if (record->tap.count && isPressed) {
      tap_code16(S(KC_BSLS));
    } else if (isHeld) {
      tap_code16(KC_END);
    }
    return false;
  case LT(0,KC_BSPC):
    if (isHeld) {
      send_mac_or_win(A(KC_BSPC), C(KC_BSPC), isPressed);
      return false;
    }
    return true;
  case MW_PSTE:
    send_mac_or_win(G(KC_V), C(KC_V), isPressed);
    return false;
  case MW_UNDO:
    send_mac_or_win(G(KC_Z), C(KC_Z), isPressed);
    return false;
  case MW_CUT:
    send_mac_or_win(G(KC_X), C(KC_X), isPressed);
    return false;
  case MW_COPY:
    send_mac_or_win(G(KC_C), C(KC_C), isPressed);
    return false;
  case CLS_WIN:
    send_mac_or_win(G(KC_W), C(KC_W), isPressed);
    return false;
  case MW_REDO:
    send_mac_or_win(G(S(KC_Z)), C(KC_Y), isPressed);
    return false;
  case KC_DWRD:
    send_mac_or_win(A(KC_BSPC), C(KC_BSPC), isPressed);
    return false;
  case KC_3GRV: {
    if (isPressed) {
      for (uint8_t i = 0; i < 3; i++) tap_code16(KC_GRV);
    }
    return false;
  }
  case LT(0,KC_E):
    if (isHeld) {
      tap_code16(NO_AE); // Intercept hold function to send æ
      return false;
    }
    return true; 	// Return true for normal processing of tap keycode
  case LT(0,KC_A):
    if (isHeld) {
      tap_code16(NO_OSTR); // Intercept hold function to send ø
      return false;
    }
    return true; 	// Return true for normal processing of tap keycode
  case LT(0,KC_O):
    if (isHeld) {
      tap_code16(NO_ARNG); // Intercept hold function to send å
      return false;
    }
    return true; 	// Return true for normal processing of tap keycode
  }
  return true;
}

